<?phpnamespace Loytor\Wxhelper\Component;use Loytor\Wxhelper\AbstractComponent;class ApiAuthorizerToken extends AbstractComponent{    const REQUEST_BASE = "https://api.weixin.qq.com/cgi-bin/component/api_authorizer_token";    public function execute($authorizer_appid, $authorizer_refresh_token)    {        $result = $this->_postData($this->getRequestUrl(self::REQUEST_BASE), [            'component_appid' => $this->getComponentId(),            'authorizer_appid' => $authorizer_appid,            'authorizer_refresh_token' => $authorizer_refresh_token        ]);        if (isset($result['errcode'])) {            return false;        } else {            return $result;        }    }}