<?phpnamespace Loytor\Wxhelper\Component;use Loytor\Wxhelper\AbstractComponent;class ApiCreatePreauthcode extends AbstractComponent{    public function __construct($component_appid, $component_access_token)    {        parent::__construct($component_appid, $component_access_token);        $this->request_url = 'https://api.weixin.qq.com/cgi-bin/component/api_create_preauthcode';    }    public function execute()    {        $result = $this->_postData($this->getRequestUrl(), [            'component_appid' => $this->getComponentId()        ]);        if (isset($result['pre_auth_code'])) {            return $result['pre_auth_code'];        } else {            return false;        }    }}