<?phpnamespace Loytor\Wxhelper\Component;use Loytor\Wxhelper\AbstractComponent;class ApiCreatePreauthcode extends AbstractComponent{    const REQUEST_BASE = "https://api.weixin.qq.com/cgi-bin/component/api_create_preauthcode";    public function execute()    {        $result = $this->_postData($this->getRequestUrl(self::REQUEST_BASE), [            'component_appid' => $this->getComponentId()        ]);        if (isset($result['pre_auth_code'])) {            return $result['pre_auth_code'];        } else {            return false;        }    }}