<?phpnamespace Loytor\Wxhelper\Component;use Loytor\Wxhelper\AbstractComponent;class ApiGetAuthorizerOption extends AbstractComponent{    const REQUEST_BASE = "https://api.weixin.qq.com/cgi-bin/component/api_get_authorizer_option";    /**     * 获取授权方的选项设置信息     * @param String $authorizer_appid 微信公众平台的APPID     * @param String $option_name 选项配置名称     * @return mixed|null|string     */    public function execute($authorizer_appid, $option_name)    {        $result = $this->_postData($this->getRequestUrl(self::REQUEST_BASE), [            'component_appid' => $this->getComponentId(),            'authorizer_appid' => $authorizer_appid,            'option_name' => $option_name        ]);        if (isset($result['errcode'])) {            return false;        } else {            return $result;        }    }}