<?phpnamespace Loytor\Wxhelper\Component;use Loytor\Wxhelper\AbstractComponent;class ApiQueryAuth extends AbstractComponent{    const REQUEST_BASE = "https://api.weixin.qq.com/cgi-bin/component/api_query_auth";    public function execute($authorization_code)    {        $result = $this->_postData($this->getRequestUrl(self::REQUEST_BASE), [            'component_appid' => $this->getComponentId(),            'authorization_code' => $authorization_code,        ]);        if (isset($result['errcode'])) {            return false;        } else {            return $result;        }    }}