<?phpnamespace Loytor\Wxhelper\Component;use Loytor\Wxhelper\AbstractComponent;class ApiSetAuthorizerOption extends AbstractComponent{    public function __construct($component_appid, $component_access_token)    {        parent::__construct($component_appid, $component_access_token);        $this->request_url = 'https://api.weixin.qq.com/cgi-bin/component/api_set_authorizer_option';    }    /**     * 设置授权方的选项信息     */    public function execute($authorizer_appid, $option_name, $option_value)    {        $result = $this->_postData($this->getRequestUrl(), [            'component_appid' => $this->getComponentId(),            'authorizer_appid' => $authorizer_appid,            'option_name' => $option_name,            'option_value' => $option_value        ]);        if (isset($result['errcode']) && $result['errcode'] === 0) {            return true;        } else {            return false;        }    }}