<?phpnamespace Loytor\Wxhelper\Wechat\Wxa;use Loytor\Wxhelper\AbstractWechat;class CreateWxaQrcode extends AbstractWechat{    const REQUEST_BASE = 'https://api.weixin.qq.com/wxa/getwxacodeunlimit';    /**     * @param string $scene     * @param string $page     * @param int $width     * @param bool $auto_color     * @param array $line_color     * @return bool|mixed     */    public function execute(string $scene, string $page, int $width = 430, bool $auto_color = false, array $line_color = ['r' => 0, 'g' => 0, 'b' => 0])    {        $result = $this->_getQrcode($this->getRequestUrl(self::REQUEST_BASE), [            'scene' => $scene,            'page' => $page,            'width' => $width,            'auto_color' => $auto_color,            'line_color' => $line_color        ]);        if (isset($result['errcode'])) {            return false;        } else {            return $result;        }    }}